if (count_ >= 3) {
    context.set('count_', 0)

    let jsonstring = {};
    jsonstring = {
        "serial_id": result.serial_id,
        "date": nowdate,
        "time": nowtime,
        "data": {
            "temp_out": temp_out,
            "hum_out": hum_out,
            "light_out": light_out,
            "temp_in": temp_in,
            "hum_in": hum_in,
            "light_in": light_in,
            "soil_in": soil_in
        }
    };

    msg.payload = jsonstring //JSON.stringify(jsonstring);
    msg.topic = "mqtt_data";
    return msg;
} else {
    let nminute = nowtime.split(":");
    let nowminute = Number(nminute[1]);
    let old_minute = context.get('old_nowminute') || 0;
    old_minute = Number(old_minute)

    if (old_minute != nowminute) {
        context.set('old_nowminute', nowminute);
        context.set('count_', 0);
    } else {
        context.set('count_', count_ + 1);
    }
}

