let get_status = context.get('get_status') || 0;
let n_config = context.get('get_config') || 0;
let get_mqtt = context.get('get_mqtt') || 0;
if (n_config !== 0) {
    // msg.payload = n_config.control_response;
    // return msg;
    // return false
    if (get_status !== 0) {
        if (get_status === "online") {
            // msg.payload = get_status
            // return msg
            if (log_control === 'No') {
                var cont_log = n_config.control_response;
                // delete cont_log["mode"];
                delete cont_log["serial_id"];
                delete cont_log["user_control"];
                context.set('log_control', JSON.stringify(cont_log));

                // msg.payload = context.get('log_control');
                // return msg;
                // return false;
            }
            if (get_mqtt !== 0) {
                var result = JSON.parse(get_mqtt);
                var output = JSON.stringify({
                    "mode": result.mode,
                    "dripper_1": result.dripper_1,
                    "dripper_2": result.dripper_2,
                    "dripper_3": result.dripper_3,
                    "dripper_4": result.dripper_4,
                    "fan_1": result.fan_1,
                    "fan_2": result.fan_2,
                    "fan_3": result.fan_3,
                    "fan_4": result.fan_4,
                    "foggy_1": result.foggy_1,
                    "foggy_2": result.foggy_2,
                    "spray": result.spray,
                    "shading": result.shading
                });
                // delete result["serial_id"];
                // delete result["user_control"];
                // msg.payload = context.get('log_control')+" <br> "+ output;
                // msg.payload = output
                // return msg;
                // return false;

                if (context.get('log_control') === output) {
                    // msg.payload = 'sum'
                    // return msg;
                } else {
                    var _result = JSON.parse(context.get('log_control'));
                    if (_result.mode === result.mode) { // 
                        if (result.mode === 'Manual') {
                            msg.headers = {};
                            msg.method = "POST";
                            msg.url = "http://decc-bigdata.com/smartfarm/server/insert_data/tu/insert_control.php";
                            msg.headers["content-type"] = 'application/x-www-form-urlencoded';
                            msg.headers["X-Requested-With"] = "XMLHttpRequest";
                            msg.payload = {
                                output: get_mqtt,//JSON.stringify(get_mqtt),
                                mode: result.mode,
                                load: '',
                                channel: ''
                            }
                            context.set('log_control', output);
                            return msg;
                        } else { // Auto
                            if (_result.dripper_1 !== result.dripper_1) {
                                msg.headers = {};
                                msg.method = "POST";
                                msg.url = "http://decc-bigdata.com/smartfarm/server/insert_data/tu/insert_control.php";
                                msg.headers["content-type"] = 'application/x-www-form-urlencoded';
                                msg.headers["X-Requested-With"] = "XMLHttpRequest";
                                msg.payload = {
                                    output: get_mqtt,//JSON.stringify(get_mqtt),
                                    mode: result.mode,
                                    load: 'dripper_1',
                                    channel: 1
                                }
                                context.set('log_control', output);
                                return msg;
                            }
                            else if (_result.dripper_2 !== result.dripper_2) {
                                msg.headers = {};
